import {personType} from './persontype.model';
import {employmentType} from './employment.model';
import { session } from './session.model';
import { person } from './person.model';


export class attendee extends person{
    school: string
    isEmployed: boolean
    employmentType: employmentType
    personType = personType;
    option1Selected: boolean = false;
    option2Selected: boolean = false;
    option3Selected: boolean = false;


    constructor (){
        super()
        this.school = "";
        this.isEmployed = false; 
        this.employmentType = employmentType.unset;
 }

 checkboxChanged(option: personType, isChecked: boolean) {
    switch(option) {
      case personType.student:
        this.option1Selected = isChecked;
        break;
      case personType.faculty:
        this.option2Selected = isChecked;
        break;
      case personType.Option3:
        this.option3Selected = isChecked;
        break;
    }
}
